import {Component, Inject, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {getBaseUrl} from "../../main";
import {firstValueFrom} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit{
  formGroup!: FormGroup;
  countryData: CountryData[] = [];
  loading : boolean = false;
  constructor(
    private readonly builder : FormBuilder,
    private readonly httpClient : HttpClient,
  ) {

  }
  async ngOnInit() : Promise<void>{
    this.countryData = await firstValueFrom(this.httpClient.get<CountryData[]>(getBaseUrl()+ "country"));

    console.log('country: ', this.countryData)
    this.formGroup = this.builder.group(
      {
        country : new FormControl("", [Validators.required]),
        vatRate : new FormControl("", [Validators.required]),
        checkboxVatRates : new FormControl("", [Validators.required]),
        withoutTax : new FormControl("", [Validators.required]),
        addedTax : new FormControl("", [Validators.required]),
        priceIncludedVat : new FormControl("", [Validators.required])
      }
    )

    this.loading = true;
  }
}
interface CountryData
{
  name? : string;
  vatRate? : any[];
}
<mat-card *ngIf="formGroup && loading">
  <mat-card-content [formGroup]="formGroup" class="flex-column">
    <mat-form-field>
      <mat-label>Country</mat-label>
        <div>
            <mat-select formControlName="country">
                <mat-option  *ngFor="let country of countryData">{{country.name}}</mat-option>
            </mat-select>
        </div>
    </mat-form-field>
    <div class="row ">

        <mat-radio-group formControlName="checkboxVatRates" class="flex-column">
          <mat-radio-button>Price Without VAT</mat-radio-button>
          <mat-radio-button>Value-Added Tax</mat-radio-button>
          <mat-radio-button>Price Included VAT</mat-radio-button>
        </mat-radio-group>
        <div class="flex">
          <input type="text"/><br/>
          <input type="text"/><br/>
          <input type="text"/><br/>
        </div>
    </div>
  </mat-card-content>
</mat-card>
