import {Component, Inject, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {getBaseUrl} from "../../main";
import {firstValueFrom} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit{
  formGroup!: FormGroup;
  countryData: CountryData[] = [];
  loading : boolean = false;
  vatList : any[] = [];
  constructor(
    private readonly builder : FormBuilder,
    private readonly httpClient : HttpClient,
  ) {

  }
  async ngOnInit() : Promise<void>{
    this.countryData = await firstValueFrom(this.httpClient.get<CountryData[]>(getBaseUrl()+ "country"));

    console.log('country: ', this.countryData)
    this.formGroup = this.builder.group(
      {
        country : new FormControl("", [Validators.required]),
        vatRate : new FormControl("", [Validators.required]),
        checkboxVatRates : new FormControl("", [Validators.required]),
        withoutTax : new FormControl("", [Validators.required]),
        addedTax : new FormControl("", [Validators.required]),
        priceIncludedVat : new FormControl("", [Validators.required])
      }
    )

    this.loading = true;
  }

  loadVatRate(list : any[]){
    this.vatList = list;
  }
}
interface CountryData
{
  name? : string;
  vatRate? : any[];
}
<mat-card class="w-50" *ngIf="formGroup && loading">
  <mat-card-content [formGroup]="formGroup" class="flex-column">
    <mat-form-field>
      <mat-label>Country</mat-label>
        <div>
            <mat-select formControlName="country">
                <mat-option (onSelectionChange)="loadVatRate(country.vatRate)" *ngFor="let country of countryData">{{country.name}}</mat-option>
            </mat-select>
            <mat-checkbox></mat-checkbox>
        </div>
    </mat-form-field>
    <div class="row" style="display: flex">
        <mat-radio-group formControlName="checkboxVatRates" class="vat-options">
          <div>
            <mat-radio-button class="col-4">Price Without VAT</mat-radio-button>
            <input [disabled]="true" formControlName="withoutTax" class="col-8" type="text"/><br/>
          </div>
          <div class="flex-row">
            <mat-radio-button class="col-4">Value-Added Tax</mat-radio-button>
            <input [disabled]="true" formControlName="addedTax" class=" col-8" type="text"/><br/>
          </div>
          <div class="flex-row">
            <mat-radio-button class="col-4">Price Included VAT</mat-radio-button>
            <input [disabled]="true" formControlName="priceIncludedVat" class="col-8" type="text"/><br/>
          </div>

        </mat-radio-group>
    </div>
  </mat-card-content>
</mat-card>
