import {Component, Inject, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {getBaseUrl} from "../../main";
import {firstValueFrom, Subscription} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit{
  formGroup!: FormGroup;
  countryData: CountryData[] = [];
  loading : boolean = true;
  vatList : number[] | undefined;
  constructor(
    private readonly builder : FormBuilder,
    private readonly httpClient : HttpClient,
  ) {

  }
  async ngOnInit() : Promise<void>{
    this.countryData = await firstValueFrom(this.httpClient.get<CountryData[]>(getBaseUrl()+ "country"));

    console.log('country: ', this.countryData)
    this.formGroup = this.builder.group(
      {
        country : new FormControl("", [Validators.required]),
        vatRate : new FormControl("", [Validators.required]),
        radioRateTax : new FormControl("", [Validators.required]),
        withoutTaxText : new FormControl({value : "", disabled : true}, [Validators.required]),
        addedTaxText : new FormControl({value : "", disabled : true}, [Validators.required]),
        priceIncludedVatText : new FormControl({value : "", disabled : true}, [Validators.required])
      }
    )
    this.loading = false;
  }

  loadVatRate(list : any, event : Event){
    event.target
    this.vatList = list;
  }
  enableInputTax(){
    switch (this.formGroup.get("radioRateTax")?.value) {
      case "withoutTax":
        this.formGroup.get("withoutTaxText")?.enable();
        this.formGroup.get("addedTaxText")?.disable();
        this.formGroup.get("priceIncludedVatText")?.disable();
        break;
      case "valueAdded":
        this.formGroup.get("addedTaxText")?.enable();
        this.formGroup.get("withoutTaxText")?.disable();
        this.formGroup.get("priceIncludedVatText")?.disable();
        break;
      case "priceIncluded":
        this.formGroup.get("priceIncludedVatText")?.enable();
        this.formGroup.get("addedTaxText")?.disable();
        this.formGroup.get("withoutTaxText")?.disable();
        break;
    }
  }

  updateTaxesInput(event : Event) {
    const valueWithTax = parseInt((event.target as HTMLInputElement).value);
    console.log(this.formGroup.get("withoutTaxText")?.disabled)
    console.log(this.formGroup.get("addedTaxText")?.disabled)
    console.log(this.formGroup.get("priceIncludedVatText")?.disabled)
    if (!this.formGroup.get("withoutTaxText")?.disabled) {
      if (this.vatList) {
        const value = valueWithTax - (valueWithTax*this.vatList[0])/100;
        this.formGroup.patchValue(
          {
            "addedTaxText": this.vatList.length >= 1 ? value + (value * this.vatList[1]) / 100 : "",
            "priceIncludedVatText": this.vatList.length >= 2 ? value + (value * this.vatList[2]) / 100 : value
          })
      }
    }
    else if(!this.formGroup.get("addedTaxText")?.disabled){
      if (this.vatList) {
        const value = valueWithTax - (valueWithTax*this.vatList[1])/100;
        this.formGroup.patchValue(
          {
            "withoutTaxText": this.vatList.length >= 0 ?value + (value * this.vatList[0]) / 100 : "",
            "priceIncludedVatText" : this.vatList.length >= 2 ? value + (value * this.vatList[2]) / 100 : value})
      }
    }
    else if(!this.formGroup.get("priceIncludedVatText")?.disabled)
    {
      if (this.vatList) {
        const value = this.vatList?.length >= 2 ? valueWithTax - (valueWithTax*this.vatList[2])/100 : valueWithTax;
        this.formGroup.patchValue(
          {
            "withoutTaxText": this.vatList.length >= 0 ?value + (value * this.vatList[0]) / 100 : "",
            "addedTaxText" : this.vatList.length >= 1 ? value + (value * this.vatList[1]) / 100 : ""})
      }
    }
  }
}
interface CountryData
{
  name? : string;
  vatRate? : any[];
}
<mat-card class="w-100" *ngIf="formGroup && !loading">
  <mat-card-content [formGroup]="formGroup" class="flex-column container">
    <div class="row d-flex">
      <mat-form-field class="col-4">
        <mat-label>Country</mat-label>
          <mat-select formControlName="country" >
              <mat-option (onSelectionChange)="loadVatRate(country.vatRate, $event)" *ngFor="let country of countryData" value="{{country.name}}">{{country.name}}</mat-option>
          </mat-select>
      </mat-form-field>
      <mat-radio-group formControlName="vatRate" class="col-6 d-flex flex-lg-wrap align-content-center" *ngIf="vatList">
        <mat-label class="vat-label align-content-center">VAT rate</mat-label>
        <mat-radio-button value="{{vatRate}}" class="col-3"  *ngFor="let vatRate of vatList">{{vatRate}} %&nbsp;</mat-radio-button>
      </mat-radio-group>
    </div>
    <div class="row d-flex">
        <mat-radio-group formControlName="radioRateTax" class="vat-options" (change)="enableInputTax()">
          <div>
            <mat-radio-button value="withoutTax" class="col-4 align-content-center d-flex">Price Without VAT</mat-radio-button>
            <mat-form-field class="col-8">
              <input matInput formControlName="withoutTaxText" type="number" (change)="updateTaxesInput($event)"/><br/>
            </mat-form-field>
          </div>
          <div class="flex-row">
            <mat-radio-button value="valueAdded" class="col-4 align-content-center d-flex">Value-Added Tax</mat-radio-button>
            <mat-form-field class="col-8">
              <input matInput formControlName="addedTaxText" type="number" (change)="updateTaxesInput($event)"/><br/>
            </mat-form-field>
          </div>
          <div class="flex-row">
            <mat-radio-button value="priceIncluded" class="col-4 align-content-center d-flex">Price Included VAT</mat-radio-button>
            <mat-form-field class="col-8">
              <input matInput formControlName="priceIncludedVatText" type="number" (change)="updateTaxesInput($event)"/><br/>
            </mat-form-field>
          </div>

        </mat-radio-group>
    </div>
  </mat-card-content>
</mat-card>
