import {Component, Inject, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {getBaseUrl} from "../../main";
import {firstValueFrom} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit{
  formGroup!: FormGroup;
  countryData: CountryData[] = [];
  constructor(
    private readonly builder : FormBuilder,
    private readonly httpClient : HttpClient,
  ) {

  }
  async ngOnInit() : Promise<void>{
    this.countryData = await firstValueFrom(this.httpClient.get<CountryData[]>(getBaseUrl()+ "country"));

    console.log('country: ', this.countryData)
    this.formGroup = this.builder.group(
      {
        country : new FormControl("", [Validators.required]),
        vatRate : new FormControl("", [Validators.required]),
        checkboxVatRates : new FormControl("", [Validators.required]),
        withoutTax : new FormControl("", [Validators.required]),
        addedTax : new FormControl("", [Validators.required]),
        priceIncludedVat : new FormControl("", [Validators.required])
      }
    )
  }
}
interface CountryData
{
  name? : string;
  vatRate? : any[];
}
<mat-card *ngIf="formGroup">
  <mat-card-content [formGroup]="formGroup">
    <mat-form-field>
      <mat-label>Country</mat-label>
        <div>
            <mat-select formControlName="country" *ngFor="let country of countryData">
                <mat-option>{{country.name}}</mat-option>
            </mat-select>
        </div>
    </mat-form-field>
    <div class="row">
      <div class="col-6">
        <mat-radio-group formControlName="checkboxVatRates">
          <mat-radio-button  label="Price Without VAT"></mat-radio-button>
          <mat-radio-button  label="Value-Added Tax"></mat-radio-button>
          <mat-radio-button  label="Price Included VAT"></mat-radio-button>
        </mat-radio-group>
      </div>
      <div class="col-6">
        <input type="text"/>
        <input type="text"/>
        <input type="text"/>
      </div>
    </div>
  </mat-card-content>
</mat-card>
