import {Component, Inject, OnInit} from '@angular/core';
import {FormBuilder, FormControl, FormGroup, Validators} from "@angular/forms";
import {HttpClient} from "@angular/common/http";
import {getBaseUrl} from "../../main";
import {firstValueFrom} from "rxjs";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit{
  formGroup!: FormGroup;
  countryData: CountryData[] = [];
  loading : boolean = false;
  vatList : any[] | undefined;
  constructor(
    private readonly builder : FormBuilder,
    private readonly httpClient : HttpClient,
  ) {

  }
  async ngOnInit() : Promise<void>{
    this.countryData = await firstValueFrom(this.httpClient.get<CountryData[]>(getBaseUrl()+ "country"));

    console.log('country: ', this.countryData)
    this.formGroup = this.builder.group(
      {
        country : new FormControl("", [Validators.required]),
        vatRate : new FormControl("", [Validators.required]),
        radioWithoutTax : new FormControl("", [Validators.required]),
        radioValueAdded : new FormControl("", [Validators.required]),
        radioIncludedTax : new FormControl("", [Validators.required]),
        withoutTaxText : new FormControl({value : "", disabled : true}, [Validators.required]),
        addedTaxText : new FormControl({value : "", disabled : true}, [Validators.required]),
        priceIncludedVatText : new FormControl({value : "", disabled : true}, [Validators.required])
      }
    )

    this.loading = true;
  }

  loadVatRate(list : any){
    this.vatList = list;
  }
}
interface CountryData
{
  name? : string;
  vatRate? : any[];
}
<mat-card class="w-50" *ngIf="formGroup && loading">
  <mat-card-content [formGroup]="formGroup" class="flex-column container">
    <mat-form-field class="row">
      <mat-label>Country</mat-label>
        <div>
            <mat-select formControlName="country" *ngIf="countryData">
                <mat-option (onSelectionChange)="loadVatRate(country.vatRate)" *ngFor="let country of countryData" value="{{country.name}}">{{country.name}}</mat-option>
            </mat-select>
        </div>
    </mat-form-field>
    <mat-radio-group formControlName="vatRates" class="row" *ngIf="vatList">
      <mat-label class="vat-label col-2">VAT rate</mat-label>
      <mat-radio-button value="{{vatRate}}" class="col-3"  *ngFor="let vatRate of vatList">{{vatRate}} %&nbsp;</mat-radio-button>
    </mat-radio-group>
    <div class="row" style="display: flex">
        <mat-radio-group formControlName="checkboxVatRates" class="vat-options">
          <div>
            <mat-radio-button value="withoutTax" class="col-4">Price Without VAT</mat-radio-button>
            <input matInput formControlName="withoutTaxText" class="col-8" type="text"/><br/>
          </div>
          <div class="flex-row">
            <mat-radio-button value="valueAdded" class="col-4">Value-Added Tax</mat-radio-button>
            <input matInput formControlName="addedTaxText" class=" col-8" type="text"/><br/>
          </div>
          <div class="flex-row">
            <mat-radio-button value="priceIncluded" class="col-4">Price Included VAT</mat-radio-button>
            <input matInput formControlName="priceIncludedVatText" class="col-8" type="text"/><br/>
          </div>

        </mat-radio-group>
    </div>
  </mat-card-content>
</mat-card>
